import React, { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import IconButton from '@mui/material/IconButton';
import AddIcon from '@mui/icons-material/Add';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import DeleteIcon from '@mui/icons-material/Delete';
import "./Todo.css";




function Todo() {

    const [todos,setTodo] = useState([]);
    const [input,setInput] = useState("");
    console.log(input);

    const addTodo = (event) =>{
        event.preventDefault();
        setTodo([...todos,input]);
        setInput("");

    }


    const deleteTodo = (id) => {

        console.log(id);

        const deleteList = todos.filter((i,ind) =>{
            return ind != id;
        })

        setTodo(deleteList);

    }

  return (

  <>
  <Box className="box" >

   <TextField value={input} onChange={e => setInput(e.target.value)} className='input' variant="standard" />
   <IconButton onClick={addTodo} >
        <AddIcon  className='button'  />
      </IconButton>



      <List >


             {todos.map((todo,ind) => {
                 return(


               <div key={ind}>
                    <ListItem  disablePadding > 
                   <ListItemText className="list"  >{todo}</ListItemText>
                   <ListItemIcon><DeleteIcon className='deleteicon' onClick={() => deleteTodo(ind)} /> </ListItemIcon>
                   </ListItem>
                   </div>


      )})}







        </List>






  </Box>

  </>
  )
}

export default Todo
